import React from 'react';
import ReactDOM from 'react-dom';
import SubmitBtn from "../submit_btn";
import PropTypes from 'prop-types';


const Input = (props) => {
    const {
        placeHolder,
        helperText,
        label,
        updateData,
    } = props;
    console.log(props);
    return (
        <div className="row input_block">
        <form className='col'>
            <div className="form-group">
                <label htmlFor="urlInput" className={"input-header"} >{label}</label>
                <input type="text" className="form-control"
                       placeholder={placeHolder} onChange={() =>
                    props.updateData(this.state.baseUrl)}/>
                    <small className="form-text text-muted">{helperText}</small>
            </div>
            <SubmitBtn value={"Make short!"}/>
         </form>
        </div>
    )
};


Input.propTypes = {
    placeHolder: PropTypes.string,
    label: PropTypes.string,
    helperText: PropTypes.string,
};

export default Input;
