import React from 'react';
import './App.css';
import ReactDOM from 'react-dom';
import Input from './components/input_block/'
import Result from './components/result_block/'
import axios from 'axios'


const API_KEY = "" // код API сервера

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputUrl: "",
      shortUrl: "your URL will be here", //сокращенная URL
    }
  }

  updateData = (field, value) => {
    this.setState({ [field]: value });
  };
  submitData = () =>{
    alert('A name was submitted!');
  };
  render(){
  return (
    <div className="App">
      <h1>URL Reducer</h1>
      <Input placeHolder={"https://www.example.com/catalog"}
             label={"Enter your URL"}
             updateData={this.updateData.bind(this)}  
             parent={this.state.inputUrl}
             handler={this.submitData}/>
      <Result label={"Your short URL:"} shortUrl={this.state.shortUrl}/>
    </div>
  );
  }
}
export default App;
