import React from 'react';
import './App.css';
import ReactDOM from 'react-dom';
import Input from './components/input_block/'
import Result from './components/result_block/'
import axios from 'axios'


const API_KEY = "" // код API сервера

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputUrl: "",
      shortUrl: "your URL will be here", //сокращенная URL
    }
  }

  updateState= (field, value) => {
    this.setState({ [field]: value });
  };
  clearState = (field) => {
    this.setState({ [field]: ""});
  }
  submitData = () =>{
    const url = "" // server API;
    const param = this.state.inputUrl;
    axios.get(url,param)
        .then(data=>this.updateState)
        .catch(err=>alert("Ooops, something wrong"))
    this.clearState(this.state.inputUrl);
  };
  render(){
  return (
    <div className="App">
      <h1>URL Reducer</h1>
      <Input placeHolder={"https://www.example.com/catalog"}
             label={"Enter your URL"}
             updateData={this.updateState.bind(this)}
             parent={this.state.inputUrl}
             submitData={this.submitData}
      />
      <Result
          label={"Your short URL:"}
          shortUrl={this.state.shortUrl}
      />
    </div>
  );
  }
}
export default App;
